(function ($) {
    
    var htmlcontainer = '<div class="ui-widget"><div class="ui-corner-all" style="padding: 0 .7em;"><p><span class="ui-icon left" style="margin-right: .3em;"></span></p></div></div>';

    $.extend($.fn, {
        writeError : function(a) {
            var now = Date.parse(new Date())
            var b = typeof(a.id) != "undefined" ? a.id : $(this).attr("id") + "_error_";
            if (typeof(a.at) != "undefined" && a.at == "bottom") {
                var c = $(this).clone().appendTo((typeof(a.target) != "undefined" ? $("#" + a.target) : $(this).parent())).attr({"id" : b});
            } else {
                var c = $(this).clone().prependTo((typeof(a.target) != "undefined" ? $("#" + a.target) : $(this).parent())).attr({"id" : b});
            }
            
            var d = '<div class="ui-widget">';
            d += '<div class="ui-state-error ui-corner-all" style="padding: 0 .7em;">';
            d += "<p>";
            d += '<span class="ui-icon ui-icon-alert left" style="margin-right: .3em;"></span>';
            d += a.msg;
            d += "</p>";
            d += "</div>";
            d += "</div>";
            
            c.html(d);
            var e = typeof(a.effect) != "undefined" ? a.effect : "pulsate";
            return c.show(e);
        },
        writeAlert : function(a) {
            var now = Date.parse(new Date())
            var b = typeof(a.id) != "undefined" ? a.id : $(this).attr("id") + "_alert_";
            if (typeof(a.at) != "undefined" && a.at == "bottom") {
                var c = $(this).clone().appendTo((typeof(a.target) != "undefined" ? $("#" + a.target) : $(this).parent())).attr({"id" : b});
            } else {
                var c = $(this).clone().prependTo((typeof(a.target) != "undefined" ? $("#" + a.target) : $(this).parent())).attr({"id" : b});
            }

            var d = '<div class="ui-widget">';
            d += '<div class="ui-state-highlight ui-corner-all" style="padding: 0 .7em;">';
            d += "<p>";
            d += '<span class="ui-icon ui-icon-info left" style="margin-right: .3em;"></span>';
            d += a.msg;
            d += "</p>";
            d += "</div>";
            d += "</div>";

            c.html(d);
            var e = typeof(a.effect) != "undefined" ? a.effect : "slow";
            return c.fadeIn(e);
        }
    });
})(jQuery)